const fs = require(''fs'');
const categorizedTodo = JSON.parse(fs.readFileSync(''public/categorized_todo.json'', ''utf8''));
const templatesJson = JSON.parse(fs.readFileSync(''public/vulnerability_templates.json'', ''utf8''));

const pluginIds = [];
const missingTemplates = {};

Object.keys(categorizedTodo.categorized_todo).forEach(category => {
  categorizedTodo.categorized_todo[category].forEach(item => {
    pluginIds.push({
      id: item.pluginId,
      title: item.title,
      type: item.type,
      category: category
    });
  });
});

// Check each pluginId
pluginIds.forEach(plugin => {
  const exists = templatesJson.some(template => template.pluginId === plugin.id);
  if (!exists) {
    if (!missingTemplates[plugin.category]) {
      missingTemplates[plugin.category] = [];
    }
    missingTemplates[plugin.category].push({
      pluginId: plugin.id,
      title: plugin.title,
      type: plugin.type
    });
  }
});

console.log(''=== MISSING VULNERABILITY TEMPLATES ==='');
console.log(''Total missing:'', Object.keys(missingTemplates).reduce((sum, cat) => sum + missingTemplates[cat].length, 0));
console.log('''');

Object.keys(missingTemplates).forEach(category => {
  if (missingTemplates[category].length > 0) {
    console.log(''==='', category, ''==='');
